import React, { useState, useEffect } from 'react';

function Frontend() {
  const [backendData, setBackendData] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('/api/data') // Passe den Pfad an deine Backend-Routen an
      .then(response => response.text())
      .then(data => {
        setBackendData(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Fehler beim Abrufen der Daten vom Backend:', error);
        setLoading(false);
      });
  }, []);

  return (
    <div>
      <h1>Frontend App</h1>
      {loading ? (
        <p>Lade Daten...</p>
      ) : (
        <p>Backend-Daten: {backendData}</p>
      )}
    </div>
  );
}

export default Frontend;
